import { Dimensions, StyleSheet } from 'react-native';
import { colors } from '../../themes/colors/Colors';

const { width } = Dimensions.get('window');

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'space-evenly',
        alignItems: 'center',
        backgroundColor: colors.background,
        paddingHorizontal: 20,
    },
    logo: {
        width: width * 0.7,
        height: width * 0.7,
        maxWidth: 350,
        maxHeight: 350,
        resizeMode: 'contain',
    },
    buttonContainer: {
        width: '85%',
        alignItems: 'center',
    },
    button: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: colors.secondary,
        paddingVertical: 10,
        paddingHorizontal: 30,
        borderRadius: 10,
        shadowColor: colors.primary,
        shadowOffset: {
            width: 0,
            height: 3,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 8,
    },
    buttonText: {
        color: colors.white,
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
    },
    buttonContainerText: {
        marginTop: 20,
        color: colors.darkGray,
        fontSize: 14,
        fontWeight: '500',
        textAlign: 'center',
    }
});

export default styles;
