import React from 'react';
import { View, Text, StyleSheet, Linking, TouchableOpacity, Image } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { colors } from '../../themes/colors/colors';
import Ionicons from '@expo/vector-icons/Ionicons';

export default function Sobre() {
  const navigation = useNavigation();
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.backBtn} onPress={() => navigation.navigate('Home')}>
        <Ionicons name="arrow-back" size={28} color={colors.secondary} />
      </TouchableOpacity>
      <View style={styles.logoBox}>
        <Image source={require('../../image/logo/iconName.png')} style={styles.logo} />
      </View>
      {/* <Text style={styles.title}>AgendaTrampo</Text> */}
      <Text style={styles.desc}>Facilite o agendamento de serviços e organize seu dia a dia de forma prática e moderna.</Text>
      <View style={styles.versionBox}>
        <Ionicons name="information-circle-outline" size={18} color={colors.secondary} />
        <Text style={styles.versionText}>Versão 1.0.0</Text>
      </View>
      <View style={styles.contactBox}>
        <Text style={styles.contactTitle}>Contato</Text>
        <TouchableOpacity style={styles.contactBtn} onPress={() => Linking.openURL('mailto:contato@agendatrampo.com')}>
          <Ionicons name="mail-outline" size={20} color={colors.primary} style={{marginRight: 8}} />
          <Text style={styles.contactBtnText}>contato@agendatrampo.com</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.contactBtn} onPress={() => Linking.openURL('https://agendatrampo.com')}>
          <Ionicons name="globe-outline" size={20} color={colors.primary} style={{marginRight: 8}} />
          <Text style={styles.contactBtnText}>agendatrampo.com</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.white,
    padding: 24,
    alignItems: 'center',
    justifyContent: 'center',
  },
  backBtn: {
    position: 'absolute',
    top: 36,
    left: 16,
    zIndex: 10,
    backgroundColor: 'transparent',
    padding: 4,
  },
  logoBox: {
    alignItems: 'center',
    marginBottom: 12,
  },
  logo: {
    width: 180,
    height: 180,
    borderRadius: 20,
    marginBottom: 4,
  },
  title: {
    fontSize: 26,
    fontWeight: 'bold',
    color: colors.secondary,
    textAlign: 'center',
    marginBottom: 8,
    letterSpacing: 1,
  },
  desc: {
    fontSize: 16,
    color: colors.text,
    textAlign: 'center',
    marginBottom: 18,
    lineHeight: 22,
    maxWidth: 320,
  },
  versionBox: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 24,
    backgroundColor: '#f5f5f5',
    borderRadius: 8,
    paddingHorizontal: 12,
    paddingVertical: 4,
  },
  versionText: {
    marginLeft: 6,
    color: colors.secondary,
    fontWeight: 'bold',
    fontSize: 15,
  },
  contactBox: {
    width: '100%',
    marginTop: 8,
    alignItems: 'center',
  },
  contactTitle: {
    fontSize: 17,
    fontWeight: 'bold',
    color: colors.primary,
    marginBottom: 10,
  },
  contactBtn: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#f8f8f8',
    borderRadius: 8,
    paddingVertical: 8,
    paddingHorizontal: 16,
    marginBottom: 10,
    width: 240,
    justifyContent: 'center',
    elevation: 1,
  },
  contactBtnText: {
    color: colors.text,
    fontSize: 15,
    fontWeight: '500',
  },
});
