import { Dimensions, StyleSheet } from 'react-native';
import { colors } from '../../../themes/colors/Colors';

const { width } = Dimensions.get('window');

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: colors.background,
        paddingHorizontal: 20,
    },
    screenTitle: {
        fontSize: 26,
        color: colors.secondary,
        fontWeight: '600',
        letterSpacing: 0.5,
        marginBottom: 40,
        textAlign: 'center',
    },
    logo: {
        width: width * 0.4,
        height: width * 0.4,
        maxWidth: 250,
        maxHeight: 250,
        resizeMode: 'contain',
        marginBottom: 10,
    },
    inputContainer: {
        width: '100%',
        maxWidth: 400,
        justifyContent: 'center',
        alignItems: 'center',
        gap: 15,
        marginBottom: 25,
    },
    textInput: {
        width: '100%',
        height: 55,
        borderColor: colors.gray,
        borderWidth: 1,
        borderRadius: 12,
        paddingHorizontal: 16,
        backgroundColor: colors.white,
        fontSize: 16,
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.05,
        shadowRadius: 3.84,
        elevation: 2,
    },
    button: {
        width: '100%',
        height: 55,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: colors.secondary,
        borderRadius: 12,
        marginTop: 10,
        shadowColor: colors.primary,
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 8,
    },
    buttonDisabled: {
        backgroundColor: colors.gray,
        elevation: 0,
        shadowOpacity: 0,
    },
    textButton: {
        color: colors.white,
        fontSize: 18,
        fontWeight: '600',
        letterSpacing: 0.5,
    },
    buttonContainer: {
        width: '100%',
        maxWidth: 400,
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'row',
        marginTop: 20,
    },
    textButtonLogin: {
        fontSize: 15,
        color: colors.darkGray,
    },
    textButtonLoginLink: {
        fontSize: 15,
        color: colors.secondary,
        marginLeft: 5,
        fontWeight: '600',
    },
    buttonTerms: {
        marginTop: 10,
        padding: 5,
    },
    textButtonTerms: {
        color: colors.secondary,
        fontSize: 13,
        fontWeight: '500',
    },
    passwordHintText: {
        fontSize: 12,
        color: colors.darkGray,
        width: '100%',
        textAlign: 'left',
        marginTop: -10, // Ajusta o espaço criado pelo 'gap' do container
    },
    termsContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        width: '100%',
        marginTop: 15,
        marginBottom: 10,
    },
    checkbox: {
        width: 24,
        height: 24,
        borderWidth: 2,
        borderColor: colors.secondary,
        borderRadius: 6,
        justifyContent: 'center',
        alignItems: 'center',
        marginRight: 12,
        checkedColor: colors.secondary,
    },
    termsTextContainer: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        flex: 1,
    },
    termsText: {
        fontSize: 14,
        color: colors.darkGray,
    },
    termsLink: {
        fontSize: 14,
        color: colors.secondary,
        fontWeight: 'bold',
        textDecorationLine: 'underline',
    }
});

export default styles;
